package modelimport model.TransactionStatus.PENDINGimport java.math.BigDecimalimport java.time.LocalDatedata class Transaction @JvmOverloads constructor(    val accountId: String,    val money: BigDecimal,    val balance: BigDecimal? = null,    val transactionType: TransactionType,    var transactionStatus: TransactionStatus = PENDING,    val creationDate: LocalDate,    val requestedExecutionDate: LocalDate)